import React, { Fragment } from "react";
import { withStyles, makeStyles } from "@material-ui/core/styles";
import { Table, TextField } from "@material-ui/core";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import BPButton from "./BPButton";

const StyledTableCell = withStyles(theme => ({
  head: {
    backgroundColor: "#3f94b5",
    color: theme.palette.common.white
  },
  body: {
    fontSize: 14
  }
}))(TableCell);

const StyledTableRow = withStyles(theme => ({
  root: {
    "&:nth-of-type(odd)": {
      backgroundColor: theme.palette.action.hover
    }
  }
}))(TableRow);

const useStyles = makeStyles(theme => ({
  root: {
    "& > *": {
      margin: theme.spacing(1)
    }
  }
}));

const CommonTable = props => {
  const classes = useStyles();
  return (
    <React.Fragment>
      {props.buttonLabel && (
        <div>
          <BPButton label={props.buttonLabel} onClick={props.onClick} />
          <form className={classes.root} noValidate autoComplete="off">
            <TextField
              label="Search"
              id="outlined-size-small"
              defaultValue="Small"
              variant="outlined"
              size="small"
            />
          </form>
        </div>
      )}
      <TableContainer component={Paper}>
        <Table style={{ minWidth: 700 }} aria-label="customized table">
          <TableHead>
            <TableRow>
              {props.Header.map(row => (
                <StyledTableCell key={row}>{row}</StyledTableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {props.data &&
              props.data.length > 0 &&
              props.data.map(row => (
                <StyledTableRow key={row._id}>
                  {props.buttonLabel == "Add Category" && (
                    <Fragment>
                      <StyledTableCell component="th" scope="row">
                        {row.category_name}
                      </StyledTableCell>
                      <StyledTableCell>
                        {row.status ? "Active" : "InActive"}
                      </StyledTableCell>
                    </Fragment>
                  )}
                  {props.buttonLabel == "Add Vendor" && (
                    <Fragment>
                      <StyledTableCell component="th" scope="row">
                        {row.vendor_name}
                      </StyledTableCell>
                      <StyledTableCell>{row.mobile}</StyledTableCell>
                      <StyledTableCell>{row.email}</StyledTableCell>
                      <StyledTableCell>{row.location}</StyledTableCell>
                    </Fragment>
                  )}
                  {props.buttonLabel == "Add Service" && (
                    <Fragment>
                      <StyledTableCell component="th" scope="row">
                        {row.service_id.service_name}
                      </StyledTableCell>
                      <StyledTableCell>
                        {row.vehicle_id.vehicle_name}
                      </StyledTableCell>
                      <StyledTableCell>
                        {row.vehicle_id.vehicle_cc}
                      </StyledTableCell>
                      <StyledTableCell>{row.description}</StyledTableCell>
                      <StyledTableCell>{row.price}</StyledTableCell>
                    </Fragment>
                  )}
                  {props.buttonLabel == "Add Vehicle" && (
                    <Fragment>
                      <StyledTableCell component="th" scope="row">
                        {row.vehicle_name}
                      </StyledTableCell>
                      <StyledTableCell>{row.vehicle_cc}</StyledTableCell>
                      <StyledTableCell>
                        {row.status ? "Active" : "InActive"}
                      </StyledTableCell>
                    </Fragment>
                  )}
                  {props.page == "orders" && (
                    <Fragment>
                      <StyledTableCell component="th" scope="row">
                        {row.mobile_number}
                      </StyledTableCell>
                      <StyledTableCell>{row.location}</StyledTableCell>
                      <StyledTableCell>{row.vehicle_name}</StyledTableCell>
                      <StyledTableCell component="th" scope="row">
                        {row.vehicle_cc}
                      </StyledTableCell>
                      <StyledTableCell>
                        {row.product_id.description.join()}
                      </StyledTableCell>
                      <StyledTableCell>
                        {row.product_id.total_amount}
                      </StyledTableCell>
                      <StyledTableCell>
                        {row.product_id.total_tax}
                      </StyledTableCell>
                    </Fragment>
                  )}
                  {props.buttonLabel && (
                    <StyledTableCell
                      style={{
                        textDecoration: "underLine",
                        color: "#3f94b5",
                        cursor: "pointer"
                      }}
                      onClick={() => props.Update(row)}
                    >
                      Edit
                    </StyledTableCell>
                  )}
                </StyledTableRow>
              ))}
          </TableBody>
        </Table>
      </TableContainer>
    </React.Fragment>
  );
};

export default CommonTable;
