import React from "react";
import {
  ListGroup,
  ListGroupItem,
  Row,
  Col,
  Form,
  FormInput,
  FormGroup,
  Button
} from "shards-react";
import Select from "react-select";

class CompleteFormExample extends React.Component {
  constructor(props) {
    super(props);

    this.toggle = this.toggle.bind(this);

    this.state = {
      dropdown1: false,
      dropdown2: false
    };
  }

  toggle(which) {
    const newState = { ...this.state };
    newState[which] = !this.state[which];
    this.setState(newState);
  }

  render() {
    const options = [
      { value: "Active", label: "Active" },
      { value: "InActive", label: "InActive" }
    ];
    return (
      <ListGroup flush>
        <ListGroupItem className="p-3">
          <Row>
            <Col>
              <Form>
                <FormGroup>
                  <label htmlFor="feInputAddress">Category Name</label>
                  <FormInput id="feInputAddress" placeholder="" />
                </FormGroup>

                <FormGroup>
                  <label htmlFor="feInputAddress2">Status</label>
                  <Select options={options} />
                </FormGroup>

                <Button type="submit">Create New Category</Button>
              </Form>
            </Col>
          </Row>
        </ListGroupItem>
      </ListGroup>
    );
  }
}

export default CompleteFormExample;
