import React, { Fragment } from "react";
import CommonTable from "../components/common/CommonTable";
import { Orders_Api } from "../api/orders_api";
import { Service_Api } from "../api/service_api";
import { Vehicle_Api } from "../api/vehicle_api";
import AddMobileDialog from "../components/forms/addMobile";
import BPNotification from "../components/common/BPNotification";
import { requestDataFormat } from "../components/common/ErrorFormat";

const HeaderValue = [
  "Mobile",
  "Location",
  "Service",
  "Description",
  "Amount",
  "Action"
];

const ordersTest = {
  _id: ObjectID("5f98719551a1290229c6d89d"),
  orderDetails: [
    {
      service_details: {
        vehicle_cc: "upto 150cc",
        model_year: "2007",
        vehicle_owner_name: "Mathan",
        vehicle_no: "TN72BM8890"
      },
      _id: ObjectID("5f98718251a1290229c6d89b"),
      user_id: ObjectID("5f948839c693fe0006aa93cf"),
      description:
        "water wash, oil change, air filter cleaning, break cleaning, chain clean and lube, cables clean and lube,carburettor cleaning, wax",
      category_name: "bike service",
      price: 100,
      brand: "TVS",
      location: "Chennai",
      service_id: "5f97e18587aac8007a6c97c2",
      mobile_number: "9500726731",
      address: "2/3 East street",
      service_type: "bike service",
      created_date: ISODate("2020-10-27T19:14:10.523Z"),
      modified_date: ISODate("2020-10-27T19:14:10.523Z"),
      __v: 0
    },
    {
      _id: ObjectID("5f98718751a1290229c6d89c"),
      user_id: ObjectID("5f948839c693fe0006aa93cf"),
      description: "voltage check,jump start of car battery",
      category_name: "battery service",
      price: 180,
      brand: "TVS",
      location: "Chennai",
      service_id: "5f97e201d493b100a6659b51",
      mobile_number: "9500726731",
      address: "2/3 East street",
      service_type: "battery service",
      created_date: ISODate("2020-10-27T19:14:15.995Z"),
      modified_date: ISODate("2020-10-27T19:14:15.995Z"),
      __v: 0
    }
  ],
  created_date: ISODate("2020-10-27T19:14:29.327Z"),
  modified_date: ISODate("2020-10-27T19:14:29.327Z"),
  __v: 0
};

const Orders = () => {
  const [modalOpen, setmodalOpen] = React.useState(false);
  const [data, setData] = React.useState([]);
  const [successModal, setSuccessModal] = React.useState(false);
  const [message, setMessage] = React.useState(false);
  const [loading, setLoading] = React.useState(false);
  const [orderId, setOrderId] = React.useState("");
  const [btnLoading, setBtnLoading] = React.useState(false);

  const getData = async () => {
    const orders = await Orders_Api.Get_Orders();
    const service = await Service_Api.Get_Service();
    const vehicle = await Vehicle_Api.Get_Vehicle();
    if (orders.code === 200) {
      orders.data.message.map(row => {
        const vehicle1 = vehicle.data.messsage.filter(
          x => x._id === row.product_id.vehicle_id
        )[0];
        const service1 = service.data.message.filter(
          x => x.service_id._id === row.product_id.service_id
        )[0];
        row = Object.assign(
          row,
          { vehicle_name: vehicle1.vehicle_name },
          { service_name: service1.service_id.service_name }
        );
      });
      setData(ordersTest);
      //setData(orders.data.message);
      setLoading(false);
    } else {
      setLoading(false);
      return [];
    }
  };

  const closeModal = () => {
    setmodalOpen(!modalOpen);
  };

  const submit = async fields => {
    setBtnLoading(true);
    const res = await Orders_Api.verifyMobile(orderId, fields);
    if (res.code === 200) {
      setmodalOpen(false);
      setBtnLoading(false);
      showToast(requestDataFormat.errorHandler(res.data.message));
      getData();
    } else {
      showToast(requestDataFormat.errorHandler(res.data.error.message));
      setBtnLoading(false);
    }
  };

  const Update = id => {
    setOrderId(id);
    setmodalOpen(true);
  };

  const showToast = msg => {
    setSuccessModal(true);
    setTimeout(() => {
      setSuccessModal(false);
    }, 3000);
    setMessage(msg);
  };

  React.useEffect(() => {
    setLoading(true);
    getData();
  }, []);

  return (
    <Fragment>
      <CommonTable
        loading={loading}
        Update={Update}
        Header={HeaderValue}
        data={data}
        page="orders"
      />
      {modalOpen && (
        <AddMobileDialog
          closeModal={closeModal}
          btnLoading={btnLoading}
          submit={fields => submit(fields)}
          showToast={showToast}
        />
      )}
      {successModal && <BPNotification message={message} />}
    </Fragment>
  );
};

export default Orders;
