import React, { Fragment } from "react";
import CommonTable from "../components/common/CommonTable";
import { Orders_Api } from "../api/orders_api";
import { Service_Api } from "../api/service_api";
import { Vehicle_Api } from "../api/vehicle_api";
import AddMobileDialog from "../components/forms/addMobile";
import BPNotification from "../components/common/BPNotification";

const HeaderValue = [
  "Mobile",
  "Location",
  "Vehicle Name",
  "Service",
  "Description",
  "Amount",
  "Tax",
  "Action"
];

const Orders = () => {
  const [modalOpen, setmodalOpen] = React.useState(false);
  const [data, setData] = React.useState([]);
  const [successModal, setSuccessModal] = React.useState(false);
  const [message, setMessage] = React.useState(false);
  const [loading, setLoading] = React.useState(false);

  const getData = async () => {
    setLoading(true);
    const orders = await Orders_Api.Get_Orders();
    const service = await Service_Api.Get_Service();
    const vehicle = await Vehicle_Api.Get_Vehicle();
    if (orders.code == 200) {
      orders.data.message.map(row => {
        const vehicle1 = vehicle.data.messsage.filter(
          x => x._id == row.product_id.vehicle_id
        )[0];
        const service1 = service.data.message.filter(
          x => x.service_id._id == row.product_id.service_id
        )[0];
        row = Object.assign(
          row,
          { vehicle_name: vehicle1.vehicle_name },
          { service_name: service1.service_id.service_name }
        );
      });
      setData(orders.data.message);
      setLoading(false);
    } else {
      setLoading(false);
      return [];
    }
  };

  const closeModal = () => {
    setmodalOpen(!modalOpen);
  };

  const submit = async fields => {
    const res = await Orders_Api.verifyMobile(fields);
    if (res.code == 200) {
      setmodalOpen(false);
      setSuccessModal(true);
      setTimeout(() => {
        setSuccessModal(false);
      }, 3000);
      setMessage(res.data.message);
      getData();
    }
  };

  React.useEffect(() => {
    getData();
  }, []);

  return (
    <Fragment>
      <CommonTable
        loading={loading}
        Header={HeaderValue}
        data={data}
        page="orders"
      />
      {modalOpen && (
        <AddMobileDialog
          closeModal={closeModal}
          submit={fields => submit(fields)}
        />
      )}
      {successModal && <BPNotification message={message} />}
    </Fragment>
  );
};

export default Orders;
