import React, { Fragment } from "react";
import { withStyles, makeStyles } from "@material-ui/core/styles";
import { Table, TextField } from "@material-ui/core";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import Autocomplete from "@material-ui/lab/Autocomplete";
import BPButton from "./BPButton";
import "./styles.css";

const StyledTableCell = withStyles(theme => ({
  head: {
    backgroundColor: "#3f94b5",
    color: theme.palette.common.white
  },
  body: {
    fontSize: 14
  }
}))(TableCell);

const StyledTableRow = withStyles(theme => ({
  root: {
    "&:nth-of-type(odd)": {
      backgroundColor: theme.palette.action.hover
    }
  }
}))(TableRow);

const useStyles = makeStyles(theme => ({
  root: {
    "& > *": {
      margin: theme.spacing(1)
    }
  }
}));

const CommonTable = props => {
  const classes = useStyles();
  return !props.loading ? (
    <React.Fragment>
      {props.buttonLabel && (
        <div style={{ display: "flex" }}>
          {props.page !== "service" && (
            <BPButton label={props.buttonLabel} onClick={props.onClick} />
          )}
          <form className={classes.root} noValidate autoComplete="off">
            {props.filterOption && (
              <Autocomplete
                id="free-solo-demo"
                freeSolo
                style={{ width: "170px", marginTop: "-15px" }}
                onChange={props.onchangeOption}
                options={props.filterOption.map(option => option)}
                renderInput={params => (
                  <TextField
                    {...params}
                    label="Search..."
                    margin="normal"
                    size="small"
                    variant="outlined"
                  />
                )}
              />
            )}
          </form>
        </div>
      )}
      <TableContainer component={Paper} style={{ marginBottom: "20px" }}>
        <Table style={{ minWidth: 700 }} aria-label="customized table">
          <TableHead>
            <TableRow>
              {props.Header.map(row => (
                <StyledTableCell key={row}>{row}</StyledTableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {props.data &&
              props.data.length > 0 &&
              props.data.map(row => (
                <StyledTableRow key={row._id}>
                  {props.buttonLabel === "Add Category" && (
                    <Fragment>
                      <StyledTableCell component="th" scope="row">
                        {row.category_name}
                      </StyledTableCell>
                      <StyledTableCell>
                        {row.status ? "Active" : "InActive"}
                      </StyledTableCell>
                    </Fragment>
                  )}
                  {props.buttonLabel === "Add Vendor" && (
                    <Fragment>
                      <StyledTableCell component="th" scope="row">
                        {row.vendor_name}
                      </StyledTableCell>
                      <StyledTableCell>{row.mobile}</StyledTableCell>
                      <StyledTableCell>{row.email}</StyledTableCell>
                      <StyledTableCell>{row.location}</StyledTableCell>
                    </Fragment>
                  )}
                  {props.buttonLabel === "Add Service" && (
                    <Fragment>
                      <StyledTableCell component="th" scope="row">
                        {row.service_name}
                      </StyledTableCell>
                      <StyledTableCell component="th" scope="row">
                        {row.category_id.category_name}
                      </StyledTableCell>
                      {row.service_type === "bike service" && (
                        <StyledTableCell>
                          {row.vehicle_id.vehicle_cc}
                        </StyledTableCell>
                      )}
                      <StyledTableCell>{row.description}</StyledTableCell>
                      <StyledTableCell>{row.price}</StyledTableCell>
                    </Fragment>
                  )}
                  {props.buttonLabel === "Add Vehicle" && (
                    <Fragment>
                      <StyledTableCell>{row.vehicle_cc}</StyledTableCell>
                      <StyledTableCell>
                        {row.status ? "Active" : "InActive"}
                      </StyledTableCell>
                    </Fragment>
                  )}
                  {props.buttonLabel === "Add Location" && (
                    <Fragment>
                      <StyledTableCell>{row.location}</StyledTableCell>
                      <StyledTableCell>
                        {row.status ? "Active" : "InActive"}
                      </StyledTableCell>
                    </Fragment>
                  )}
                  {props.page === "orders" && (
                    <Fragment>
                      <StyledTableCell component="th" scope="row">
                        {row.mobile_number}
                      </StyledTableCell>
                      <StyledTableCell>{row.location}</StyledTableCell>
                      <StyledTableCell component="th" scope="row">
                        {row.category_name}
                      </StyledTableCell>
                      <StyledTableCell>{row.description}</StyledTableCell>
                      <StyledTableCell>{row.price}</StyledTableCell>
                    </Fragment>
                  )}
                  {props.page === "orders" &&
                    (row.isVerified ? (
                      <StyledTableCell>Verified</StyledTableCell>
                    ) : (
                      <StyledTableCell
                        style={{
                          textDecoration: "underLine",
                          color: "#3f94b5",
                          cursor: "pointer"
                        }}
                        onClick={id => props.Update(row.orderId)}
                      >
                        Verify Mobile
                      </StyledTableCell>
                    ))}
                  {props.buttonLabel && (
                    <StyledTableCell
                      style={{
                        textDecoration: "underLine",
                        color: "#3f94b5",
                        cursor: "pointer"
                      }}
                      onClick={() => props.Update(row)}
                    >
                      Edit
                    </StyledTableCell>
                  )}
                </StyledTableRow>
              ))}
          </TableBody>
        </Table>
      </TableContainer>
    </React.Fragment>
  ) : (
    <div className="preloader">
      <img
        src="http://i.imgur.com/KUJoe.gif"
        className="rotate"
        width="70"
        alt=""
        height="70"
      />
    </div>
  );
};

export default CommonTable;
