import React from "react";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import BPText from "../common/BPText";
import BPSelect from "../common/BPSelect";
import BPButton from "../common/BPButton";
import DialogTitle from "@material-ui/core/DialogTitle";

export default function AddCategoryDialog(props) {
  const [name, setName] = React.useState("");
  const [status, setStatus] = React.useState("Active");
  const options = [
    { value: "Active", label: "Active" },
    { value: "InActive", label: "InActive" }
  ];

  const onChangeName = e => {
    setName(e.target.value);
  };

  const onChangeStatus = e => {
    setStatus(e.target.value);
  };

  const submit = () => {
    const fields = {
      data: {
        category_name: name,
        status: email
      }
    };
    props.submit(fields);
  };

  return (
    <div>
      <Dialog
        open={true}
        // onClose={handleClose}
        aria-labelledby="form-dialog-title"
      >
        <DialogTitle id="form-dialog-title">Add Category</DialogTitle>
        <DialogContent>
          <BPText
            autoFocus={true}
            onChange={e => onChangeName(e)}
            id="name"
            value={name}
            label="Category Name"
            type="text"
          />
          <Select
            autoFocus
            inputProps={{
              name: "max-width",
              id: "max-width"
            }}
          >
            <MenuItem value="true">Active</MenuItem>
            <MenuItem value="false">InActive</MenuItem>
          </Select>
        </DialogContent>
        <DialogActions>
          <BPButton onClick={props.closeModal} label={"Cancel"} />
          <BPButton onClick={() => submit()} label={"Add Category"} />
        </DialogActions>
      </Dialog>
    </div>
  );
}
