import React, { Fragment } from "react";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import BPText from "../common/BPText";
import BPButton from "../common/BPButton";
import DialogTitle from "@material-ui/core/DialogTitle";
import Select from "@material-ui/core/Select";
import MenuItem from "@material-ui/core/MenuItem";

export default function AddServiceDialog(props) {
  const [name, setName] = React.useState(
    (props.fields && props.fields.service_id.service_name) || ""
  );
  const [description, setDescription] = React.useState(
    props.fields.description || ""
  );
  const [price, setPrice] = React.useState(props.fields.price || "");
  const [tax, setTax] = React.useState(props.fields.tax || "");
  const [category, setCategory] = React.useState("");
  const [vehicleName, setvehicleName] = React.useState("");
  const [vehicleCC, setVehicleCC] = React.useState("");
  const [vehicleCCList, setVehicleCCList] = React.useState([]);

  const onChangeName = e => {
    setName(e.target.value);
  };

  const onChangeDescription = e => {
    setDescription(e.target.value);
  };

  const onChangePrice = e => {
    setPrice(e.target.value);
  };

  const onChangeTax = e => {
    setTax(e.target.value);
  };

  const onChangeCategory = e => {
    setCategory(e.target.value);
  };

  const onChangeVehicleName = async e => {
    setvehicleName(e.target.value);
    const res = await props.vehicleCC(e.target.value);
    setVehicleCCList(res);
  };

  const onChangeVehicleCC = e => {
    setVehicleCC(e.target.value);
  };

  const submit = () => {
    if (
      name === "" ||
      name === undefined ||
      description === "" ||
      description === undefined ||
      price === "" ||
      price === undefined ||
      tax === "" ||
      tax === undefined
    ) {
      showToast("Please fill all the fields");
    } else {
      if (props.fields) {
        const fields = {
          data: {
            service_name: name,
            description: description,
            price: price,
            tax: tax
          }
        };
        props.update(fields, props.fields._id);
      } else {
        const fields = {
          data: {
            service_name: name,
            category_name: category,
            vehicle_name: vehicleName,
            vehicle_cc: vehicleCC,
            description: description,
            price: price,
            tax: tax
          }
        };
        props.submit(fields);
      }
    }
  };

  return (
    <div>
      <Dialog
        open={true}
        // onClose={handleClose}
        aria-labelledby="form-dialog-title"
      >
        <DialogTitle id="form-dialog-title">Add Service</DialogTitle>
        <DialogContent>
          <BPText
            autoFocus={true}
            onChange={e => onChangeName(e)}
            id="name"
            value={name}
            label="Service Name"
            type="text"
          />
          {!props.fields && (
            <Fragment>
              <Select
                autoFocus
                onChange={onChangeCategory}
                value={category}
                style={{
                  width: "100%",
                  marginTop: "12px"
                }}
                inputProps={{
                  name: "max-width",
                  id: "max-width"
                }}
              >
                {props.category &&
                  props.category.map(row => {
                    return (
                      <MenuItem key={row._id} value={row.category_name}>
                        {row.category_name}
                      </MenuItem>
                    );
                  })}
              </Select>
              <Select
                autoFocus
                onChange={onChangeVehicleName}
                value={vehicleName}
                style={{
                  width: "100%",
                  marginTop: "12px"
                }}
                inputProps={{
                  name: "max-width",
                  id: "max-width"
                }}
              >
                {props.vehicle &&
                  props.vehicle.map(row => {
                    return (
                      <MenuItem key={row._id} value={row.vehicle_name}>
                        {row.vehicle_name}
                      </MenuItem>
                    );
                  })}
              </Select>
              <Select
                autoFocus
                onChange={onChangeVehicleCC}
                value={vehicleCC}
                style={{
                  width: "100%",
                  marginTop: "12px"
                }}
                inputProps={{
                  name: "max-width",
                  id: "max-width"
                }}
              >
                {vehicleCCList &&
                  vehicleCCList.map(row => {
                    return (
                      <MenuItem key={row._id} value={row.vehicle_cc}>
                        {row.vehicle_cc}
                      </MenuItem>
                    );
                  })}
              </Select>
            </Fragment>
          )}
          <BPText
            onChange={e => onChangeDescription(e)}
            id="description"
            value={description}
            label="Description"
            type="text"
          />
          <BPText
            onChange={e => onChangePrice(e)}
            id="price"
            value={price}
            label="Price"
            type="text"
          />
          <BPText
            onChange={e => onChangeTax(e)}
            id="tax"
            value={tax}
            label="Tax"
            type="text"
          />
        </DialogContent>
        <DialogActions>
          <BPButton onClick={props.closeModal} label={"Cancel"} />
          <BPButton
            onClick={() => submit()}
            loading={props.btnLoading}
            label={props.fields ? "Edit Service" : "Add Service"}
          />
        </DialogActions>
      </Dialog>
    </div>
  );
}
