import React from "react";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import BPText from "../common/BPText";
import BPButton from "../common/BPButton";
import DialogTitle from "@material-ui/core/DialogTitle";

export default function AddVendorDialog(props) {
  const [name, setName] = React.useState("");
  const [mobile, setMobile] = React.useState("");
  const [email, setEmail] = React.useState("");
  const [location, setLocation] = React.useState("");
  const [address, setAddress] = React.useState("");

  const onChangeName = e => {
    setName(e.target.value);
  };

  const onChangeMobile = e => {
    setMobile(e.target.value);
  };

  const onChangeEmail = e => {
    setEmail(e.target.value);
  };

  const onChangeLocation = e => {
    setLocation(e.target.value);
  };

  const onChangeAddress = e => {
    setAddress(e.target.value);
  };

  const submit = () => {
    const fields = {
      data: {
        vendor_id: 1,
        name: name,
        email: email,
        address: address,
        location: location
      }
    };
    props.submit(fields);
  };

  return (
    <div>
      <Dialog
        open={true}
        // onClose={handleClose}
        aria-labelledby="form-dialog-title"
      >
        <DialogTitle id="form-dialog-title">Add Vendor</DialogTitle>
        <DialogContent>
          <BPText
            autoFocus={true}
            onChange={e => onChangeName(e)}
            id="name"
            value={name}
            label="Vendor Name"
            type="text"
          />
          <BPText
            onChange={e => onChangeMobile(e)}
            id="mobile"
            value={mobile}
            pattern="[1-9]{1}[0-9]{9}"
            label="Mobile Number"
            type="number"
          />
          <BPText
            onChange={e => onChangeEmail(e)}
            id="email"
            value={email}
            label="Email Address"
            type="email"
          />
          <BPText
            onChange={e => onChangeAddress(e)}
            id="address"
            label="Address"
            type="text"
          />
          <BPText
            onChange={e => onChangeLocation(e)}
            id="location"
            value={location}
            label="Location"
            type="text"
          />
        </DialogContent>
        <DialogActions>
          <BPButton onClick={props.closeModal} label={"Cancel"} />
          <BPButton onClick={() => submit()} label={"Add Vendor"} />
        </DialogActions>
      </Dialog>
    </div>
  );
}
