import React from "react";
import store from "store";
import CommonTable from "../components/common/CommonTable";
import AddVendorDialog from "../components/forms/addVendor";
import { Vendor_Api } from "../api/vendor_api";
import BPNotification from "../components/common/BPNotification";

const HeaderValue = ["Vendor Name", "Mobile", "Email", "Location", "Action"];

const Vendor = props => {
  const [modalOpen, setmodalOpen] = React.useState(false);
  const [successModal, setSuccessModal] = React.useState(false);
  const [message, setMessage] = React.useState(false);
  const [row, setRow] = React.useState(false);
  const [data, setData] = React.useState([]);
  const addVendor = () => {
    setmodalOpen(!modalOpen);
  };

  const closeModal = () => {
    setmodalOpen(!modalOpen);
  };

  const getData = async () => {
    const res = await Vendor_Api.Get_Vendor();
    if (res.code == 200) {
      setData(res.data.message);
    } else {
      return [];
    }
  };

  const submit = async fields => {
    const res = await Vendor_Api.Add_Vendor(fields);
    if (res.code == 200) {
      setmodalOpen(false);
      setSuccessModal(true);
      setMessage(res.data.message);
      getData();
    }
  };

  const Update = row => {
    setRow(row);
    setmodalOpen(true);
  };

  const Update_Vendor = async (fields, id) => {
    const row1 = data.filter(x => x._id == id)[0];
    const data1 = {
      vendor_name: row1.vendor_name,
      email: row1.email,
      mobile: row1.mobile,
      location: row1.location
    };
    console.log(row1);
    console.log(fields);
    const difference = Object.keys(row1).filter(
      k => data1[k] !== fields.data[k]
    );
    console.log(difference);
    // const row = {
    //   data: {
    //     vendor_name: fields.vendor_name,
    //     email: fields.email,
    //     mobile: fields.mobile,
    //     location: fields.location
    //   }
    // };
    // const res = await Vendor_Api.Update_Vendor(fields._id, row);
    // if (res.code == 200) {
    //   setmodalOpen(false);
    //   setSuccessModal(true);
    //   setMessage(res.data.message);
    //   getData();
    // } else {
    //   setMessage(res.data.error.message);
    // }
  };

  React.useEffect(() => {
    getData();
  }, []);

  return (
    <React.Fragment>
      <CommonTable
        Header={HeaderValue}
        data={data}
        buttonLabel={"Add Vendor"}
        Update={row => Update(row)}
        onClick={res => addVendor(res)}
      />
      {modalOpen && (
        <AddVendorDialog
          closeModal={closeModal}
          fields={row}
          update={(updated, id) => Update_Vendor(updated, id)}
          submit={fields => submit(fields)}
        />
      )}
      {successModal && <BPNotification message={message} />}
    </React.Fragment>
  );
};

export default Vendor;
