import React from "react";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import BPText from "../common/BPText";
import BPButton from "../common/BPButton";
import DialogTitle from "@material-ui/core/DialogTitle";
import Select from "@material-ui/core/Select";
import MenuItem from "@material-ui/core/MenuItem";

export default function AddServiceDialog(props) {
  const [name, setName] = React.useState("");
  const [description, setDescription] = React.useState("");
  const [price, setPrice] = React.useState("");
  const [tax, setTax] = React.useState("");
  const [category, setCategory] = React.useState("");
  const [service, setService] = React.useState("");

  const onChangeName = e => {
    setName(e.target.value);
  };

  const onChangeDescription = e => {
    setDescription(e.target.value);
  };

  const onChangePrice = e => {
    setPrice(e.target.value);
  };

  const onChangeTax = e => {
    setTax(e.target.value);
  };

  const onChangeCategory = e => {
    setCategory(e.target.value);
  };

  const onChangeService = e => {
    setService(e.target.value);
    const res = props.vehicleCC();
    console.log(res);
  };

  const submit = () => {
    const fields = {
      // data: {
      //   vendor_name: name,
      //   email: email,
      //   mobile: mobile,
      //   location: location
      // }
    };
    props.submit(fields);
  };

  return (
    <div>
      <Dialog
        open={true}
        // onClose={handleClose}
        aria-labelledby="form-dialog-title"
      >
        <DialogTitle id="form-dialog-title">Add Service</DialogTitle>
        <DialogContent>
          <BPText
            autoFocus={true}
            onChange={e => onChangeName(e)}
            id="name"
            value={name}
            label="Service Name"
            type="text"
          />
          <Select
            autoFocus
            onChange={onChangeCategory}
            value={category}
            style={{
              width: "100%",
              marginTop: "12px"
            }}
            inputProps={{
              name: "max-width",
              id: "max-width"
            }}
          >
            {props.category &&
              props.category.map(row => {
                return (
                  <MenuItem value={row.category_name}>
                    {row.category_name}
                  </MenuItem>
                );
              })}
          </Select>
          <Select
            autoFocus
            onChange={onChangeService}
            value={service}
            style={{
              width: "100%",
              marginTop: "12px"
            }}
            inputProps={{
              name: "max-width",
              id: "max-width"
            }}
          >
            {props.service &&
              props.service.map(row => {
                return (
                  <MenuItem value={row.service_id.service_name}>
                    {row.service_id.service_name}
                  </MenuItem>
                );
              })}
          </Select>
          <BPText
            onChange={e => onChangeDescription(e)}
            id="description"
            value={description}
            label="Description"
            type="text"
          />
          <BPText
            onChange={e => onChangePrice(e)}
            id="price"
            value={price}
            label="Price"
            type="text"
          />
          <BPText
            onChange={e => onChangeTax(e)}
            id="tax"
            value={tax}
            label="Tax"
            type="text"
          />
        </DialogContent>
        <DialogActions>
          <BPButton onClick={props.closeModal} label={"Cancel"} />
          <BPButton onClick={() => submit()} label={"Add Vendor"} />
        </DialogActions>
      </Dialog>
    </div>
  );
}
