import React from "react";
import CommonTable from "../components/common/CommonTable";
import { Service_Api } from "../api/service_api";
import { Category_Api } from "../api/category_api";
import { Vehicle_Api } from "../api/vehicle_api";
import AddServiceDialog from "../components/forms/addService";
import BPNotification from "../components/common/BPNotification";

const HeaderValue = [
  "Service Name",
  "Category",
  "Vehicle CC",
  "Description",
  "Price",
  "Actions"
];

const Service = () => {
  const [data, setData] = React.useState([]);
  const [modalOpen, setmodalOpen] = React.useState(false);
  const [successModal, setSuccessModal] = React.useState(false);
  const [message, setMessage] = React.useState(false);
  const [row, setRow] = React.useState(false);

  const addService = () => {
    setRow(false);
    setmodalOpen(!modalOpen);
  };

  const closeModal = () => {
    setmodalOpen(!modalOpen);
  };

  const getData = async () => {
    const res = await Service_Api.Get_Service();
    const category = await Category_Api.Get_Category();
    const vehicle = await Vehicle_Api.Get_Vehicle();
    if (res.code == 200) {
      setData(res.data.message);
    } else {
      return [];
    }
  };

  const submit = async fields => {
    const res = await Service_Api.Add_Servie(fields);
    if (res.code == 200) {
      setmodalOpen(false);
      setSuccessModal(true);
      setTimeout(() => {
        setSuccessModal(false);
      }, 3000);
      setMessage(res.data.message);
      getData();
    }
  };

  const Update_Vendor = async (fields, id) => {
    const row1 = data.filter(x => x._id == id)[0];
    let data1 = {};
    if (row1.vendor_name !== fields.data.vendor_name) {
      data1 = Object.assign(data1, { vendor_name: fields.data.vendor_name });
    }
    if (row1.email !== fields.data.email) {
      data1 = Object.assign(data1, { email: fields.data.email });
    }
    if (row1.mobile !== fields.data.mobile) {
      data1 = Object.assign(data1, { mobile: fields.data.mobile });
    }
    if (row1.location !== fields.data.location) {
      data1 = Object.assign(data1, { location: fields.data.location });
    }
    const obj = {
      data: data1
    };
    const res = await Service_Api.Update_Service(row1._id, obj);
    if (res.code == 200) {
      setmodalOpen(false);
      setSuccessModal(true);
      setTimeout(() => {
        setSuccessModal(false);
      }, 3000);
      setMessage(res.data.message);
      getData();
    } else {
      setMessage(res.data.error.message);
    }
  };

  React.useEffect(() => {
    getData();
  }, []);

  return (
    <React.Fragment>
      <CommonTable
        data={data}
        Header={HeaderValue}
        buttonLabel={"Add Service"}
        onClick={res => addService(res)}
      />
      {modalOpen && (
        <AddServiceDialog
          closeModal={closeModal}
          fields={row}
          update={(updated, id) => Update_Vendor(updated, id)}
          submit={fields => submit(fields)}
        />
      )}
      {successModal && <BPNotification message={message} />}
    </React.Fragment>
  );
};

export default Service;
