import React from "react";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import BPText from "../common/BPText";
import BPButton from "../common/BPButton";
import DialogTitle from "@material-ui/core/DialogTitle";

export default function AddVendorDialog(props) {
  const [name, setName] = React.useState("");
  const [mobile, setMobile] = React.useState("");
  const [email, setEmail] = React.useState("");
  const [location, setLocation] = React.useState("");

  const onChangeName = e => {
    setName(e.target.value);
  };

  const onChangeMobile = e => {
    setMobile(e.target.value);
  };

  const onChangeEmail = e => {
    setEmail(e.target.value);
  };

  const onBlurEmail = e => {
    var pattern = new RegExp(
      /^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i
    );
    if (!pattern.test(e.target.value)) {
      setEmailError("true");
    } else {
      setEmailError("false");
    }
  };

  const onChangeLocation = e => {
    setLocation(e.target.value);
  };

  const submit = () => {
    const fields = {
      data: {
        vendor_name: name,
        email: email,
        mobile: mobile,
        location: location
      }
    };
    props.submit(fields);
  };

  return (
    <div>
      <Dialog
        open={true}
        // onClose={handleClose}
        aria-labelledby="form-dialog-title"
      >
        <DialogTitle id="form-dialog-title">Add Vendor</DialogTitle>
        <DialogContent>
          <BPText
            autoFocus={true}
            onChange={e => onChangeName(e)}
            id="name"
            value={name}
            label="Vendor Name"
            type="text"
          />
          <BPText
            onChange={e => onChangeMobile(e)}
            id="mobile"
            value={mobile}
            pattern="[1-9]{1}[0-9]{9}"
            label="Mobile Number"
            type="number"
          />
          <BPText
            onChange={e => onChangeEmail(e)}
            onBlur={onBlurEmail}
            id="email"
            value={email}
            label="Email Address"
            type="email"
          />
          <BPText
            onChange={e => onChangeLocation(e)}
            id="location"
            value={location}
            label="Location"
            type="text"
          />
        </DialogContent>
        <DialogActions>
          <BPButton onClick={props.closeModal} label={"Cancel"} />
          <BPButton onClick={() => submit()} label={"Add Vendor"} />
        </DialogActions>
      </Dialog>
    </div>
  );
}
