import React from "react";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import BPText from "../common/BPText";
import BPButton from "../common/BPButton";
import DialogTitle from "@material-ui/core/DialogTitle";

export default function AddMobileDialog(props) {
  const [mobile, setMobile] = React.useState("");

  const onChangeMobile = e => {
    if (e.target.value.length <= 10) {
      setMobile(e.target.value);
    }
  };

  const submit = () => {
    if (mobile !== "" && mobile !== undefined) {
      const fields = {
        data: {
          mobile_number: mobile
        }
      };
      props.submit(fields);
    } else {
      props.showToast("Please fill mobile number");
    }
  };

  return (
    <div>
      <Dialog open={true} aria-labelledby="form-dialog-title">
        <DialogTitle id="form-dialog-title">
          Enter vendor's mobile number
        </DialogTitle>
        <DialogContent>
          <BPText
            type="number"
            pattern="[0-9]*"
            inputmode="numeric"
            autoFocus={true}
            onChange={e => onChangeMobile(e)}
            id="mobile"
            value={mobile}
            label="Mobile Number"
          />
        </DialogContent>
        <DialogActions style={{ padding: "18px" }}>
          <BPButton onClick={props.closeModal} label={"Cancel"} />
          <BPButton
            loading={props.btnLoading}
            onClick={() => submit()}
            label={"Verify"}
          />
        </DialogActions>
      </Dialog>
    </div>
  );
}
